CREATE TABLE compañia.material(
	codigo_material varchar(30),
	nombre varchar(100),
	descripcion varchar(30),
	zona varchar(30),
	nicho varchar(30),
	primary key(codigo_material)
);

CREATE TABLE compañia.proveedor(
	rut_p varchar(30),
	nombre varchar(60),
	direccion varchar(100),
	telefono varchar(30),
	primary key(rut_p)
);

CREATE TABLE compañia.pedido_proveedor(
	id_pedido varchar(30),
	rut_p varchar(30),
	fecha_pedido varchar(30),
	total_pedido integer,
	foreign key(rut_p) references Compañia.proveedor(rut_p),
	primary key(id_pedido)
);

CREATE TABLE compañia.albaran(
	id_albaran varchar(30),
	id_pedido varchar(30),
	fecha_entrega varchar(30),
	total integer,
	foreign key(id_pedido) references Compañia.pedido_proveedor(id_pedido),
	primary key(id_albaran)
);

CREATE TABLE compañia.pedido_prov_material(
	id_pedido varchar(30),
	codigo_material varchar(30),
	cantidad integer,
	precio integer,
	foreign key(id_pedido) references Compañia.pedido_proveedor(id_pedido),
	foreign key(codigo_material) references Compañia.material(codigo_material)
);

CREATE TABLE compañia.albaran_material(
	id_albaran varchar(30),
	codigo_material varchar(30),
	cantidad integer,
	precio integer,
	foreign key(id_albaran) references Compañia.albaran(id_albaran),
	foreign key(codigo_material) references Compañia.material(codigo_material)
);

CREATE TABLE compañia.cliente(
	rut_c varchar(60),
	nombre varchar(60),
	direccion varchar(100),
	telefono varchar(60),
	codigo_postal integer,
	primary key(rut_c)
);

CREATE TABLE compañia.presupuesto(
	id_presupuesto varchar(30),
	rut_c varchar(30),
	total_presupuesto integer,
	rebaja varchar(30),
	fecha_presupuesto varchar(30),
	foreign key(rut_c) references Compañia.cliente(rut_c),
	primary key(id_presupuesto)
);

CREATE TABLE compañia.pedido_cliente(
	codigo varchar(30),
	id_presupuesto varchar(30),
	total_pedido_c integer,
	fecha_pedido_c varchar(30),
	pagado bit,
	facturado bit,
	foreign key(id_presupuesto) references Compañia.presupuesto(id_presupuesto),
	primary key(codigo)
);

CREATE TABLE compañia.pres_incluye_mat(
	codigo_material varchar(30),
	id_presupuesto varchar(30),
	cantidad integer,
	precio integer,
	foreign key(codigo_material) references Compañia.material(codigo_material),
	foreign key(id_presupuesto) references Compañia.presupuesto(id_presupuesto)
);

